export default {
	checkworkshift: () => {
		clearStore();
		//show workshiftall
		GetAll_work.run(
			() => {
				let shiftBox = moment(first_date.text).weekday() - 1;
				let result = GetAll_work.data;
				let total = GetAll_work.data.length;
				for(let i=0; i < total;i++){
					let day = Number(moment(result[i].date).format("D")) + Number(shiftBox);
					let key = "box"+day.toString();
					if(result[i].S_id == 1){
						if(result[i].staff_total >= 9){
							storeValue("box"+day,'#bbf7d0');
						}else{
							storeValue("box"+day,'#fef08a');
						}
					}else if(result[i].S_id == 2){
						if(result[i].staff_total >= 9){
							storeValue("boxn"+day,'#bbf7d0');
						}else{
							storeValue("boxn"+day,'#fef08a');
						}
					}else if(result[i].S_id == 3){
						if(result[i].staff_total >= 9){
							storeValue("boxnn"+day,'#bbf7d0');
						}else{
							storeValue("boxnn"+day,'#fef08a');
						}
					}
				}
			}
		);
	},
}
