export default {
	checkworkshift: async () => {
		storeValue('shift_moring', []);
		storeValue('shift_afternoon', []);
		storeValue('shift_night', []);
		//show workshiftall
    await GetAll_work.run();
		if(GetAll_work.data.length > 0){
			let shiftBox = moment(first_date.text).weekday() - 1;

			GetAll_work.data.map(async (item) => {
      	let day = Number(moment(item.date).format("D")) + Number(shiftBox)-1;

        if(item.S_id == 1){
            if(item.staff_total >= 9){
               await storeValue(...appsmith.store.shift_moring,{'day': day, color: '#bbf7d0'});
            }else{
               await storeValue(...appsmith.store.shift_moring,{'day': day, color: '#fef08a'});
            }
        }

        if(item.S_id == 2){
            if(item.staff_total >= 9){
               await storeValue(...appsmith.store.shift_night,{'day': day, color: '#bbf7d0'});
            }else{
               await storeValue(...appsmith.store.shift_afternoon,{'day': day, color: '#fef08a'});
            }
        }

        if(item.S_id == 3){
            if(item.staff_total >= 9){
                await storeValue('shift_night', {'day': day, color: '#bbf7d0'});
            }else{
                await storeValue('shift_night', {'day': day, color: '#fef08a'});
            }
        }

			})

			console.log(appsmith.store.shift_night);


		}

	},
}
